		private static String COMMA_DELIMITER = ",";
		

		public void parseSHAPEValues(File path){

			try { 
				BufferedReader br = new BufferedReader(new FileReader(path));
				String line;
				String fn = path.getName();
				int j = fn.lastIndexOf('.');
				if (j!=-1)
				{ fn = fn.substring(0,j); }

				while ((line = br.readLine()) != null) {
					String[] data = line.split(COMMA_DELIMITER);
					if (data.length>1)
					{	
						try {
							int index = Integer.parseInt(data[0])-1;
							double val = Double.parseDouble(data[1]);
								_gpRef.getRNA().setValue(index, val);
						} catch (NumberFormatException e) {
							// Nan
						}
					}
				}
				br.close();
				_gpModel.getRNA().setValues(_gpRef.getRNA().getValues());
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			} catch (IOException e) {
				e.printStackTrace();
			}
		}

		private void saveSHAPEValues(String name) {
			_vals.put(name, _gpRef.getRNA().getValues());
			_probing.addItem(name);
		}

		private void setSHAPE(String s) {
			if (!_gpRef.getProfileDescription().equals(s))
			{
				_gpRef.getRNA().setValues(_vals.get(s));
				_gpRef.setUseColorMap(true);
				_gpRef.setProfileDescription(s);
				_gpRef.repaint();
			}
			if (!_gpModel.getProfileDescription().equals(s))
			{
				_gpModel.getRNA().setValues(_vals.get(s));
				_gpModel.setUseColorMap(true);
				_gpModel.repaint();
				_gpModel.setProfileDescription(s);
			}
			updateBorders();
		}
		

		private void setModel(GenomeModel g, PANEL_APPLICATION_OPTIONS tt) {
				try {
					for(GenomePanel gp : getPanels(tt)) {
						
						gp.setRNA(g);
						gp.setStructureDescription(g.getDescription());
						gp.repaint();
					}
					updateBorders();
				} catch (NumberFormatException e) {
				}


		}

		private void setSequence(File path, PANEL_APPLICATION_OPTIONS tt) {
			try { 
				String fn = path.getName();
				int j = fn.lastIndexOf('.');
				if (j!=-1)
				{ fn = fn.substring(0,j); }
				BufferedReader br = new BufferedReader(new FileReader(path));
				String line;

				while ((line = br.readLine()) != null) {
					String s = line.trim();
					if (s.length()>1) {
						char c = s.charAt(0);
						if ((c!='>') && (c!='.') && (c!='(') && (c!='[')) {
							try {
								for(GenomePanel gp : getPanels(tt)) {
									gp.setSequence(s);
									gp.repaint();
								}
								updateBorders();
							} catch (NumberFormatException e) {
							}
							
						}
					}
				}
				br.close();
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			} catch (IOException e) {
				e.printStackTrace();
			}

		}
		
		


		public static void setUIFont (javax.swing.plaf.FontUIResource f){
		    java.util.Enumeration keys = UIManager.getDefaults().keys();
		    while (keys.hasMoreElements()) {
		      Object key = keys.nextElement();
		      Object value = UIManager.get (key);
		      if (value instanceof javax.swing.plaf.FontUIResource)
		        UIManager.put (key, f);
		      }
		    } 
		
		
