		@Override
		public void onGenomePanelChanged(GenomePanelChange tt, GenomePanel source) {
			GenomePanel other = _gpModel;
			if (source==other) {
				other = this._gpRef;
			}
			switch(tt) {
				case SHIFT_CHANGED:
					Couple<Integer,Integer> interval =  source.getVisibleInterval();
					//other.showPart(interval.first,interval.second);
					break;
				case ZOOM_CHANGED:
					if (this._syncZoom.isSelected())
					{ other.setZoomLevel(source.getZoomLevel()); }
					break;					
			}
			repaint();
		}

